hudson.tasks.Shell.command:
echo "OSS Run Container Mockup Web"
export PATH=$PATH:/usr/local/bin/
eval "$(docker-machine env default)"
function check_container(){
docker_name=$1 if [ ! $docker_name = "" ]; then docker ps -a | grep "${docker_name}" | awk '{print $1}' | xargs docker rm else echo "Container $docker_name does not exist." fi }
function run_container(){
docker_name=$1
docker_name=$1 if [ ! $docker_name = "" ]; then docker ps -a | grep "${docker_name}" | awk '{print $1}' | xargs docker rm else echo "Container $docker_name does not exist." fi
/usr/local/bin/docker run -P --privileged=true -v \ /Users/traqy/github/test-teralytics-docker-wp-nginx/volume-data/upload:/var/www/upload \ --name=${docker_name} traqy/${docker_name} /root/scripts/apps.sh
}
run_container 'acme-app-mockup'
